import React from "react";
import { useState } from "react";

const Search = ({ events, searchEvent }) => {
  const [searchInput, setSearchInput] = useState("");
  const [searchResult, setSearchResult] = useState([]);

  const handleSearch = (e) => {
    setSearchInput(e.target.value);
    let searchEvents = events.filter((event) => {
      return e.target.value !== "" && event.includes(e.target.value);
    });
    setSearchResult(searchEvents);
  };
  return (
    <div id="search">
      <input
        type="text"
        id="searchInput"
        onKeyUp={searchEvent}
        value={events}
        placeholder="Search a event..."
        required
      />
        <ul
          style={
            searchResult.length === 0
              ? { display: "none" }
              : { display: "block" }
          }
        >
          {searchResult.length > 0
            ? searchResult.map((fruit, index) => <li key={index}>{fruit}</li>)
            : ""}
        </ul>
      <a id="searchIcon" href="www.google.com.tr">
        <i className="fas fa-search"></i>
      </a>
    </div>
  );
};

export default Search;
