import React from "react";
import { useState } from "react";

const Search = ({ events }) => {
  const [searchInput, setSearchInput] = useState("");
  const [searchResult, setSearchResult] = useState([]);

  const handleSearch = (e) => {
    setSearchInput(e.target.value);
    let searchEvents = events.filter((event) => {
      console.log(e.target.value);
      console.log(searchResult);
      return e.target.value !== "" && event.name.includes(e.target.value);
    });
    setSearchResult(e.target.value);
  };
  return (
    <div id="search">
      <input
        value={searchInput}
        onChange={handleSearch}
        type="text"
        id="searchInput"
        placeholder="Search a event..."
        required
      />
      <ul
        className="resultBox"
        style={
          searchResult.length === 0 ? { display: "none" } : { display: "block" }
        }
      >
        {searchResult.length > 0
          ? searchResult.map((event, index) => (
              <li key={index}>{event.name}</li>
            ))
          : ""}
      </ul>
      <a id="searchIcon" href="www.google.com.tr">
        <i className="fas fa-search"></i>
      </a>
    </div>
  );
};

export default Search;
